function MoviePoster({poster, alt}) {
    return (
        <img src={poster} alt={alt} title={alt} className="Movie__Poster"/>
    )
}

function MovieGenre({genre}) {
    return (
    <span className="Movie__Genre">{genre}</span>
    )
}
export default Movie;
