import React, { Component }  from 'react';
import PropTypes from 'prop-types';

class Map extends Component {
    
    static propTypes = {
        map : PropTypes.object.isRequired
    }

    componentDidMount() {
        this._getMap();
    }
    
    _getMap  = () => {
        let { map } = this.props;
        map = new window.Tmapv2.Map("map_div", { // 지도가 생성될 div
			width : "100%", // 지도의 넓이
			height : "400px" // 지도의 높이
        });
        
        // map 클릭 이벤트 등록
      //  map.events.register("click", map, getLonlat);
    }

    render() {
        return (
            <div>
                <div id="map_div"></div>
                <p id="result"></p>
            </div>
        
        
        )
    };
}

function getLonlat({e}) {
    const { map } =this.props;  
    const lonlat = map.getLonLatFromViewPortPx(e.xy).transform("EPSG:3857", "EPSG:4326");//클릭한 부분의 ViewPorPx를 LonLat로 변환합니다
    const result ='클릭한 위치의 좌표는'+lonlat+'입니다.'; 
    const resultDiv = document.getElementById("result");
    resultDiv.innerHTML = result;    
}
export default Map;
