import React, { Component }  from 'react';
import PropTypes from 'prop-types';

var map;
var markers = [];
const lotList = [];
const latList = [];
class Map extends Component {

    componentDidMount() {
        this._getMap();
    }
    
    _getMap  = () => {
        map = new window.Tmapv2.Map("map_div", { // 지도가 생성될 div
			width : "100%", // 지도의 넓이
			height : "400px" // 지도의 높이
        });

        map.addListener("click", getLonlat);
    }

    _getSpot = () => {
        const lotObj = this._getMaxMin(lotList);
        const latObj = this._getMaxMin(latList);
        
        console.log(lotObj.max);
        console.log(lotObj.min);
        console.log(latObj.max);
        console.log(latObj.min);
    }

    _getMaxMin = (list) => {
        var max = list[0];
        var min = list[0];
        list.map(num=> {
            if (max < num)
                max = num;
            
            if (min > num)
                min = num;
        })
        console.log(max);
        console.log(min);
        return { max , min}
    }

    
    render() {
        return (
            <div>
                <div id="map_div"></div>
                <button  onClick={() => this._getSpot()}>중간지점</button>
            </div>
        
        
        )
    };
}

function getLonlat(e)  {
    const lonlat = e.latLng;
	//Marker 객체 생성.
    const marker = new window.Tmapv2.Marker({
        position: new window.Tmapv2.LatLng(lonlat.lat(),lonlat.lng()), //Marker의 중심좌표 설정.
        map: map //Marker가 표시될 Map 설정.
    });
    markers.push(marker);
    latList.push(lonlat.lat());
    lotList.push(lonlat.lng());
}

export default Map;
