import React, { Component }  from 'react';
import PropTypes from 'prop-types';

var map;
var markers = [];
const lotList = [];
const latList = [];
var subways = [];
var totalList = [];
class Map extends Component {

    componentDidMount() {
        this._getMap();
    }
    
    _getMap  = () => {
        map = new window.Tmapv2.Map("map_div", { // 지도가 생성될 div
			width : "100%", // 지도의 넓이
            height : "400px", // 지도의 높이
            zoom : 13
            
        });

        map.addListener("click", getLonlat);
    }

    _getSpot = () => {
        const lot = this._getlotLat(lotList);
        const lat = this._getlotLat(latList); 
        this._getSubway({dSpotlat : lat, dSpotlot : lot});
        

        const marker = new window.Tmapv2.Marker({
            position: new window.Tmapv2.LatLng(lat,lot), //Marker의 중심좌표 설정.
			icon: 'http://tmapapis.sktelecom.com/upload/tmap/marker/pin_b_m_b.png', //Marker의 아이콘.
			map: map //Marker가 표시될 Map 설정.
        });    
    }

    _getSubway = async ({dSpotlat, dSpotlot}) => {
        const searchPoiInfo = await this._callCategoryApi({dSpotlat, dSpotlot, category : "지하철;"});
        subways = searchPoiInfo.pois.poi;
        var index = 0;
        await subways.map((subway, index) => {
            this._getCafe({poi : subway, key : index});
        });
        await console.log(totalList);
    }

    _callCategoryApi = ({dSpotlat, dSpotlot, category}) => {
        var url = "https://apis.openapi.sk.com/tmap/pois/search/around?version=1&"
        var params = {
            "categories" : category,
            "resCoordType" : "WGS84GEO",//응답 좌표계 유형
            "reqCoordType" : "WGS84GEO",//요청 좌표계 유형
            "centerLon" : dSpotlot,
            "centerLat" : dSpotlat,
            "appKey" : "l7xxbfb4b13f846e43b8b0924bbda1166055",
            "count" : 10,
            "radius" : 10
        }
        var esc = encodeURIComponent;
        var query = Object.keys(params)
                    .map(k => esc(k) + '=' + esc(params[k]))
                    .join('&');             
        return fetch(url +query)
        .then(response => response.json())
        .then(json => json.searchPoiInfo)
        .catch(err => console.log(err));
    }
    _getCafe = async ({poi, key}) => {
        const cafes = await this._callCafeCategoryApi({dSpotlat : parseFloat(poi.frontLat), dSpotlot : parseFloat(poi.frontLon), category : "스터디카페;카페;", i : key});
        poi.cafes = cafes;
        await this._setTotalList({totalCount : poi.cafes.totalCount, index : key}) 
    }

    _callCafeCategoryApi = ({dSpotlat, dSpotlot, category, i}) => {
        var url = "https://apis.openapi.sk.com/tmap/pois/search/around?version=1&"
        var params = {
            "categories" : category,
            "resCoordType" : "WGS84GEO",//응답 좌표계 유형
            "reqCoordType" : "WGS84GEO",//요청 좌표계 유형
            "centerLon" : dSpotlot,
            "centerLat" : dSpotlat,
            "appKey" : "l7xxbfb4b13f846e43b8b0924bbda1166055",
            "count" : 10,
            "radius" : 10
        }
        var esc = encodeURIComponent;
        var query = Object.keys(params)
                    .map(k => esc(k) + '=' + esc(params[k]))
                    .join('&');             
        return fetch(url +query)
        .then(response => response.json())
        .then(json => json.searchPoiInfo)
        //.than(result => this._setTotalList({ totalCount : result.totalCount, index : i}))
        .catch(err => console.log(err));
    }

    _setTotalList({totalCount, index}) {
        totalList[totalCount] = index;
        
    }
     

    _getlotLat = (list) => {
        var max = list[0];
        var min = list[0];
        list.map(num=> {
            if (max < num)
                max = num;
            
            if (min > num)
                min = num;
        })

        return min +((max - min) / 2)
    }

    
    render() {
        return (
            <div>
                <div id="map_div"></div>
                <button  onClick={() => this._getSpot()}>중간지점</button>
            </div>
        
        
        )
    };
}

function getLonlat(e)  {
    const lonlat = e.latLng;
	//Marker 객체 생성.
    const marker = new window.Tmapv2.Marker({
        position: new window.Tmapv2.LatLng(lonlat.lat(),lonlat.lng()), //Marker의 중심좌표 설정.
        map: map //Marker가 표시될 Map 설정.

    });
    markers.push(marker);
    console.log(lonlat);
    latList.push(lonlat.lat());
    lotList.push(lonlat.lng());
}

export default Map;
